describe KassociationsController do

  # This should return the minimal set of attributes required to create a valid
  # Kassociation. As you add validations to Kassociation, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all kassociations as @kassociations" do
      kassociation = Kassociation.create! valid_attributes
      get :index
      assigns(:kassociations).should eq([kassociation])
    end
  end

  describe "GET show" do
    it "assigns the requested kassociation as @kassociation" do
      kassociation = Kassociation.create! valid_attributes
      get :show, :id => kassociation.id
      assigns(:kassociation).should eq(kassociation)
    end
  end

  describe "GET new" do
    it "assigns a new kassociation as @kassociation" do
      get :new
      assigns(:kassociation).should be_a_new(Kassociation)
    end
  end

  describe "GET edit" do
    it "assigns the requested kassociation as @kassociation" do
      kassociation = Kassociation.create! valid_attributes
      get :edit, :id => kassociation.id
      assigns(:kassociation).should eq(kassociation)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Kassociation" do
        expect {
          post :create, :kassociation => valid_attributes
        }.to change(Kassociation, :count).by(1)
      end

      it "assigns a newly created kassociation as @kassociation" do
        post :create, :kassociation => valid_attributes
        assigns(:kassociation).should be_a(Kassociation)
        assigns(:kassociation).should be_persisted
      end

      it "redirects to the created kassociation" do
        post :create, :kassociation => valid_attributes
        response.should redirect_to(Kassociation.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved kassociation as @kassociation" do
