        Kassociation.any_instance.stub(:save).and_return(false)
        post :create, :kassociation => {}
        assigns(:kassociation).should be_a_new(Kassociation)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Kassociation.any_instance.stub(:save).and_return(false)
        post :create, :kassociation => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested kassociation" do
        kassociation = Kassociation.create! valid_attributes
        # Assuming there are no other kassociations in the database, this
        # specifies that the Kassociation created on the previous line
