        Kassociation.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => kassociation.id, :kassociation => {'these' => 'params'}
      end

      it "assigns the requested kassociation as @kassociation" do
        kassociation = Kassociation.create! valid_attributes
        put :update, :id => kassociation.id, :kassociation => valid_attributes
        assigns(:kassociation).should eq(kassociation)
      end

      it "redirects to the kassociation" do
        kassociation = Kassociation.create! valid_attributes
        put :update, :id => kassociation.id, :kassociation => valid_attributes
        response.should redirect_to(kassociation)
      end
    end

    describe "with invalid params" do
      it "assigns the kassociation as @kassociation" do
